import React, { Component } from 'react';
import Tree from "./tree";
import "./panel.css";
import {Button} from "react-bootstrap";
import {Collapse} from "react-bootstrap";


class TreePanel extends Component {
    constructor() {
        super();
        this.state = {civ: "Aztecs", open: true}
        this.collapse = this.collapse.bind(this);
        this.getImg = this.getImg.bind(this);
        console.log("Current civ si: " +this.props.civ) 
    }

    /* collapse panel on clicking the header */
    collapse() {
        let x = (this.state.open) ? false : true;
        this.setState({open: x});
    }

    /* generate building icon for the header of the panel */
    getImg() {
        if (this.props.build !== "Economic Buildings") {
            let x = this.props.build.toLowerCase().replace(' ', '_');
            return x;
        } else {
            let x = "farm";
            return x;
        }
    }

    
    render() {
        
        return (
            
            <th className="panel">
                <Button onClick={this.collapse} className="panel-btn" aria-controls="example-collapse-text" aria-expanded={!this.state.open}>
                    <img className="img" src={require("../Buildings/" + this.getImg() + ".png")}></img>{" "}{this.props.build}
                </Button>
                <Collapse in={this.state.open}>
                    <div>
                    <div id="example-collapse-text" className="panel-space">
                    <Tree units={this.props.tree}/>
                    
                    </div>

                    {/* <div id="example-collapse-text" className="panel-space">
                    <img src={require("../Techs/castle_age.png")} className="age"></img>
                    </div>

                    <div id="example-collapse-text" className="panel-space">
                    <img src={require("../Techs/imperial_age.png")} className="age"></img>Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus
                    </div> */}
                    
                    </div>
                </Collapse>
            </th>
            
        )
    }
}

export default TreePanel;
