import React, {Component} from "react";
import Match from "./match";



class MatchPanel extends Component {

    constructor() {
        super();
        this.state = {team1 : [], team2 : []};
        this.getMatch = this.getMatch.bind(this);
    }

    getMatch() {
        let m = new Match(1);
        let opponent = m.setTeams();
        console.log(m.matchID + " " + m.matchStatus);
        this.setState({team1 : m.team1, team2 : m.team2})
    }

    render() {
        return(
            <div>
                <button onClick={this.getMatch} className="btn btn-dark member-btn"> Find Match </button>
                <div><br></br>
                    <a onClick={this.getMatch} className="btn btn-dark member-btn"> {this.state.team1[0].name} </a>
                    <h5> VS </h5>
                    <a onClick={this.getMatch} className="btn btn-dark member-btn"> {this.state.team2[0].name} </a>
                </div>
            </div>
        )
    }
}

export default MatchPanel;
