import React, {Component} from "react";

class Tree extends Component {
    constructor() {
        super();
        this.state = {units : []}
        this.defaultTree = this.defaultTree.bind(this);
        this.generateUnit = this.generateUnit.bind(this);
        this.defaultTree();
    }

    defaultTree() {
        let dark = [];
        let feudal = ["Archer", "Skirmisher"];
        let castle = ["Crossbowman", "Elite Skirmisher", "Cavalry Archer"];
        let imperial = ["Arbalester", "NA", "Heavy Cavalry Archer", "Hand Cannoneer"];
        let postImp = [];
        // var archer = [2, "Archer", "Crossbowman"Heavy Cavalry Archer, "Arbalester"];
        // var skirm = [2, "Skirmisher", "Elite Skirmisher"];
        // var ca = [3, "Cavalry Archer", "Heavy Cavalry Archer"];
        // var hc = [4, "Hand Cannoneer"];
        
        // this.units.archer = archer;
        // this.units.skirm = skirm;
        // this.units.ca = ca;
        // this.units.hc = hc;

        // this.units.push(archer, skirm, ca, hc);
        let x = [];
        x.push(dark, feudal, castle, imperial, postImp);
        this.setState({units : x});
        console.log(this.units);
    }

    generateUnit() {
        this.state.units[1].map(u => {
            let uImg = "./Units/" + u.toLowerCase().replace(' ', '_') + ".png";
            let img = <img src={require(uImg)}></img>
            return (img);
        })
    }

    render() {
        return(
            <div>
                {this.generateUnit()}
            </div>
        )
    }
}

export default Tree;
