import React, {Component} from "react";
import "./civ.css";
import DATA from "./data";

class CivInfo extends Component {

    generateInfo() {
        let info;
        let civID = DATA.civs[this.props.civ];
        info = DATA.key_value[civID];
        return info;
    }
    
    render() {
        return(
            <div className="box info btn-dark">
                
                <br></br>
                <h6 className="infoline"> Civilization Bonus: </h6>
                <p>{this.props.civ}</p>
                <article className="info-text">{this.generateInfo()}</article>
                <h6 className="infoline"> Unique Unit: </h6>
                <p>...</p>
                <h6 className="infoline"> Unique Tech: </h6>
                <p>...</p>
                <h6 className="infoline"> Team Bonus: </h6>
                <p>...</p>                
            </div>
        )
    }
}

export default CivInfo;
