import React, {Component} from "react";


let dark = ["NA"];
let feudal = ["Archer", "Skirmisher"];
let castle = ["Crossbowman", "Elite Skirmisher", "Cavalry Archer", "NA", "NA", "Thumb Ring"];
let imperial = ["Arbalester", "NA", "Heavy Cavalry Archer", "Hand Cannoneer", "NA", "Parthian Tactics"];
let postImp = ["NA"];

let x = [];
x.push(dark, feudal, castle, imperial, postImp);

class Tree extends Component {
    constructor(props) {
        super(props);
        this.state = {units : []}
        this.defaultTree = this.defaultTree.bind(this);
        this.generateUnit = this.generateUnit.bind(this);
               
    }

    defaultTree() {
        // let dark = ["Archer", "Skirmisher"];
        // let feudal = ["Archer", "Skirmisher"];
        // let castle = ["Crossbowman", "Elite Skirmisher", "Cavalry Archer", "NA", "NA", "Thumb Ring"];
        // let imperial = ["Arbalester", "NA", "Heavy Cavalry Archer", "Hand Cannoneer", "NA", "Parthian Tactics"];
        // let postImp = ["Archer", "Skirmisher"];
        // var archer = [2, "Archer", "Crossbowman"Heavy Cavalry Archer, "Arbalester"];
        // var skirm = [2, "Skirmisher", "Elite Skirmisher"];
        // var ca = [3, "Cavalry Archer", "Heavy Cavalry Archer"];
        // var hc = [4, "Hand Cannoneer"];
        
        // this.units.archer = archer;
        // this.units.skirm = skirm;
        // this.units.ca = ca;
        // this.units.hc = hc;

        // this.units.push(archer, skirm, ca, hc);
        let x = [];
        x.push(dark, feudal, castle, imperial, postImp);
        this.setState({units : x});
        console.log(this.state.units);
    }

    generateUnit(i) {
        if (this.props.units !== undefined) {
            return this.props.units[i].map(u => {
                let uImg = u.toLowerCase().replace(' ', '_').replace(' ', '_');
                if (u !== "NA") {
                    let img = <img className="unit" src={require("../Units/" + uImg + ".png")}></img>
                    return (img);
                } else {
                    let img = <img className="unit unit-na" src={require("../Units/" + uImg + ".png")}></img>
                    return (img);
                }  
            }) 
        }
        
    }

    render() {
        return(
         
            <div className="tree">
            <div><img src={require("../Techs/dark_age.png")} className="age"></img>
                {this.generateUnit(0)}
                
            </div>
            <div><img src={require("../Techs/feudal_age.png")} className="age"></img>
                {this.generateUnit(1)}

            </div>
            <div><img src={require("../Techs/castle_age.png")} className="age"></img>
                {this.generateUnit(2)}

            </div>
            <div><img src={require("../Techs/imperial_age.png")} className="age"></img>
                {this.generateUnit(3)}
            </div>
            <div><img src={require("../Techs/imperial_age.png")} className="age age-empty"></img>
                {this.generateUnit(4)}

            </div>
            </div>
            
        )
    }
}

export default Tree;
