import React, {Component} from "react";


let dark = [];
let feudal = ["Archer", "Skirmisher"];
let castle = ["Crossbowman", "Elite Skirmisher", "Cavalry Archer"];
let imperial = ["Arbalester", "NA", "Heavy Cavalry Archer", "Hand Cannoneer"];
let postImp = [];

let x = [];
x.push(dark, feudal, castle, imperial, postImp);

class Tree extends Component {
    constructor() {
        super();
        this.state = {units : x}
        this.defaultTree = this.defaultTree.bind(this);
        this.generateUnit = this.generateUnit.bind(this);        
    }

    defaultTree() {
        let dark = [];
        let feudal = ["Archer", "Skirmisher"];
        let castle = ["Crossbowman", "Elite Skirmisher", "Cavalry Archer"];
        let imperial = ["Arbalester", "NA", "Heavy Cavalry Archer", "Hand Cannoneer"];
        let postImp = [];
        // var archer = [2, "Archer", "Crossbowman"Heavy Cavalry Archer, "Arbalester"];
        // var skirm = [2, "Skirmisher", "Elite Skirmisher"];
        // var ca = [3, "Cavalry Archer", "Heavy Cavalry Archer"];
        // var hc = [4, "Hand Cannoneer"];
        
        // this.units.archer = archer;
        // this.units.skirm = skirm;
        // this.units.ca = ca;
        // this.units.hc = hc;

        // this.units.push(archer, skirm, ca, hc);
        let x = [];
        x.push(dark, feudal, castle, imperial, postImp);
        this.setState({units : x});
        console.log(this.state.units);
    }

    generateUnit(i) {
        return this.state.units[i].map(u => {
            let uImg = u.toLowerCase().replace(' ', '_').replace(' ', '_');
            if (u !== "NA") {
                let img = <span>  <img className="unit" src={require("../Units/" + uImg + ".png")}></img></span>
                return (img);
            } else {
                let img = <span>  <img className="unit unit-na" src={require("../Units/" + uImg + ".png")}></img></span>
                return (img);
            }  
        }) 
    }

    render() {
        return(
            <div className="tree">
            <div><img src={require("../Techs/feudal_age.png")} className="age"></img><div></div>
                {this.generateUnit(0)}
            </div><span> </span>
            <div><img src={require("../Techs/feudal_age.png")} className="age"></img>
                {this.generateUnit(1)}

            </div>
            <div><img src={require("../Techs/castle_age.png")} className="age"></img>
                {this.generateUnit(2)}

            </div>
            <div><img src={require("../Techs/imperial_age.png")} className="age"></img>
                {this.generateUnit(3)}
            </div>
            <div>
                {this.generateUnit(4)}

            </div>
            </div>
        )
    }
}

export default Tree;
