import React, {Component} from "react";
import TreePanel from "./treePanel";
import {TOWNCENTRE, RANGE, BARRACKS, STABLE, SIEGE, CASTLE, BLACSKMITH, MONASTERY, UNIVERSITY, ECONOMY, DOCK, DEFENSE} from "./UNIT_NAMES";
import CivContext from "../CivContext";
const NA = "NA";

export default class TreeParent extends Component {
    xRange = RANGE;
    
    constructor(props) {
        super(props);
        this.updateTree = this.updateTree.bind(this);
    }

    updateTree(value) {

        switch (value) {
            case "Aztecs":
                this.disable(RANGE, ["Cavalry Archer", "Heavy Cav Archer", "Hand Cannoneer", "Thumb Ring", "Parthian Tactics", "Heavy Cavalry Archer"]);
                break;
                case "Franks":
                this.disable(RANGE, ["Arbalest", "Thumb Ring", "Parthian Tactics"]);
                break;
        }
        // if (value === "Aztecs" ) {
        // this.disable(RANGE, ["Cavalry Archer", "Heavy Cav Archer", "Hand Cannoneer", "Thumb Ring", "Parthian Tactics", "Heavy Cavalry Archer"]);
        // }
        // ["Cavalry Archer", "Heavy Cav Archer", "Hand Cannoneer", "Thumb Ring", "Parthian Tactics"]
    }

    disable(build, units) {
        console.log(RANGE);
        let newTree = [];
        newTree.push(build[0], build[1], build[2], build[3], build[4])
        let disableLength = units.length;

        for (let i=0; disableLength>0 && i<=4; i++) {
            newTree[i].forEach(unit => {
                units.forEach(x => {
                    if (unit === x) {
                        let k = newTree[i].indexOf(unit);
                        newTree[i][k] = NA;
                        disableLength--;
                    } 
                });
            });
        }
        console.log(newTree);
    }

    render() {
        
        return(
            <>
            <CivContext.Consumer> 
                {value => this.updateTree(value)}
            </CivContext.Consumer> 
            <div>
                <h5> HEADING </h5>
                <table>
                    <tbody>
                        <tr>
                        <TreePanel build="Archery Range" tree={RANGE}/></tr>
                        {/* <TreePanel build="Barracks" tree={barracks}/>
                        <TreePanel build="Stable" tree={stable}/>
                        
                        </tr>
                        <tr>
                        <TreePanel build="Siege Workshop" tree={siege}/>
                        <TreePanel build="Castle" tree={castle}/>
                        <TreePanel build="Defense" tree={defense}/>
                        </tr>
                        <tr>
                        <TreePanel build="Blacksmith" tree={blacksmith}/>
                        <TreePanel build="University" tree={university}/>
                        <TreePanel build="Monastery" tree={monastery}/>
                        </tr>
                        <tr>
                        <TreePanel build="Town Center" tree={townCentre}/>
                        <TreePanel build="Economic Buildings" tree={economy}/>
                        <TreePanel build="Dock" tree={dock}/>
                        </tr> */}
                    </tbody>
                </table>
            </div>
            </>
                    
            
        )
        
    }
}
