import React, {Component} from "react";
import MemberList from "./memberList";
import Dropdown from 'react-bootstrap/Button';
import "./member.css";
import DropdownButton from 'react-bootstrap/DropdownButton';
import MenuItem from 'react-bootstrap/DropdownItem';
import Button from 'react-bootstrap/Button';

const memberList = new MemberList();
// var list = [
//     {"name": "Ghazna", "elo": 1568, "id": 1092899123, "active": true},
//     {"name": "Ghazna", "elo": 1200, "id": 1273822123, "active": true},
//     {"name": "Ghazna", "elo": 1100, "id": 1273899003, "active": true},
//     {"name": "Ghazna", "elo": 2042, "id": 1113899123, "active": false},
//     {"name": "Ghazna", "elo": 1023, "id": 1233899123, "active": false}];

class MemberPanel extends Component {
    constructor(props) {
        super(props);
        this.state = {members: memberList.getMemberList(), count : memberList.getTotalCount(), showList: false};
        this.showMembers = this.showMembers.bind(this);
        this.displayMembers = this.displayMembers.bind(this);
         
    }

    getMembers() {
        return this.state.count;
    }

    showMembers() {
        if (!this.state.showList) {
            this.setState({showList : true});
            return this.state.members;
        } else {
            this.setState({showList : false});
        }
    }

    displayMembers() {
        if (this.state.showList) {
            let list = this.state.members.map(m => 
                <div className="select_option">
                    <a key={m.id} className="btn btn-dark member"> {"[" + m.elo + "] " + m.name} </a>
                    
                    <DropdownButton id="document-type">
                        <MenuItem>Item 1</MenuItem>
                        <MenuItem>Item 2</MenuItem>
                        <MenuItem>Item 3</MenuItem>
                        <MenuItem>Item 4</MenuItem>
                    </DropdownButton>
                    <SplitButton href="http://google.com" bsStyle="info" bsSize="large" title="Main List" >
  <MenuItem eventKey="1">Delete List</MenuItem>
  <MenuItem eventKey="2">Edit List Name</MenuItem>
</SplitButton>
                </div>
                
            )
            return (list);

            // let list = this.state.members.map(m => 
            //    <div key={m.id}><div key={m.id} className="btn btn-dark member"> {"[" + m.elo + "] " + m.name} </div><br></br></div>
            // )
            // return (list);
        }
    }

    renderMemberBtn() {
        let btnText = (!this.state.showList) ? "Show Members" : "Hide Memebrs";
        let btn = <button onClick={this.showMembers} className="btn btn-dark"> {btnText} </button>
        return btn;
    }

    render() {
        return (
            <div className="memberPanel">
                <p>Members online: {this.getMembers()} {this.renderMemberBtn()} </p>
                <div>{this.displayMembers()}</div>
            </div>
        )
    }
}

export default MemberPanel;
