import React, {Component} from "react";
import MemberList from "./memberList";
import Dropdown from 'react-bootstrap/Button';
import "./member.css";
import Button from 'react-bootstrap/Button';
import MenuItem from 'react-bootstrap/DropdownItem';
import SplitButton from 'react-bootstrap/SplitButton';


const memberList = new MemberList();

class MemberPanel extends Component {
    constructor(props) {
        super(props);
        this.state = {members: memberList.getMemberList(), count : memberList.getTotalCount(), showList: false};
        this.showMembers = this.showMembers.bind(this);
        this.displayMembers = this.displayMembers.bind(this);
         
    }

    getMembers() {
        return this.state.count;
    }

    showMembers() {
        if (!this.state.showList) {
            this.setState({showList : true});
            return this.state.members;
        } else {
            this.setState({showList : false});
        }
    }

    displayMembers() {
        if (this.state.showList) {
            let list = this.state.members.map(m => 
                <div >

                    {/* <SplitButton href="#" title={"[" + m.elo + "] " + m.name} variant="dark">
                        <button className="btn btn-dark member-btn" eventKey="1"> Friend </button><br></br>
                        <button className="btn btn-dark member-btn" eventKey="2"> Block </button><br></br>
                        <button className="btn btn-dark member-btn" eventKey="2"> Message </button><br></br>
                        <button className="btn btn-dark member-btn" eventKey="2"> History </button>
                        
                    </SplitButton> */}

                    <div key={m.id} className="btn btn-dark member"> {"[" + m.elo + "] " + m.name} </div>
                    <div key={m.id} onClick={this.showMemberMenu} className="btn btn-dark member"> ❯ </div>

                    <div class="btn-group dropup">
                        <button type="button" class="btn btn-secondary">
                            {m.name}
                        </button>
                        <button type="button" class="btn btn-secondary dropdown-toggle dropdown-toggle-split" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                            <span class="sr-only">Toggle Dropdown</span>
                        </button>
                        <div class="dropdown-menu">
                            <a class="dropdown-item" href="#">Action</a>
                            <a class="dropdown-item" href="#">Another action</a>
                            <a class="dropdown-item" href="#">Something else here</a>
                            <div class="dropdown-divider"></div>
                            <a class="dropdown-item" href="#">Separated link</a>
                        </div>
                    </div>
                    
                </div>
                
            )
            return (list);

            // let list = this.state.members.map(m => 
            //    <div key={m.id}><div key={m.id} className="btn btn-dark member"> {"[" + m.elo + "] " + m.name} </div><br></br></div>
            // )
            // return (list);
        }
    }

    renderMemberBtn() {
        let btnText = (!this.state.showList) ? "Show Members" : "Hide Memebrs";
        let btn = <button onClick={this.showMembers} className="btn btn-dark"> {btnText} </button>
        return btn;
    }

    render() {
        return (
            <div className="memberPanel">
                <p>Members online: {this.getMembers()} {this.renderMemberBtn()} </p>
                <div>{this.displayMembers()}</div>
            </div>
        )
    }
}

export default MemberPanel;
