import React, {Component} from "react";
import UnitService from "../Service/unitService";
import NotifService, { UNIT_HOVERED } from "../Service/notifService";
import ReactTooltip from 'react-tooltip';
import Tooltip from "rc-tooltip";
import 'rc-tooltip/assets/bootstrap.css';

import DATA from "../data";
const NA = "NA";

var notifService = new NotifService();
var untiService = new UnitService();
const text = <span>Tooltip Text</span>;
let dark = ["NA"];
let feudal = ["Archer", "Skirmisher"];
let castle = ["Crossbowman", "Elite Skirmisher", "Cavalry Archer", "NA", "NA", "Thumb Ring"];
let imperial = ["Arbalester", "NA", "Heavy Cavalry Archer", "Hand Cannoneer", "NA", "Parthian Tactics"];
let postImp = ["NA"];
let x = [];
x.push(dark, feudal, castle, imperial, postImp);

class Tree extends Component {
    constructor(props) {
        super(props);
        this.state = {unit: "", unitInfo: "", unitStats: {}, unitTechs: ""}
        this.defaultTree = this.defaultTree.bind(this);
        this.generateUnit = this.generateUnit.bind(this);
        this.handleUnitHover = this.handleUnitHover.bind(this);
    }

    componentDidMount() {
        notifService.addObserver(UNIT_HOVERED, this, this.reset)
    }

    componentWillUnmount() {
        notifService.removeObserver(this, UNIT_HOVERED);
    }

    reset() {
        // this.setState({civ : "Aztecs"});
    }

    defaultTree() {
        // let dark = ["Archer", "Skirmisher"];
        // let feudal = ["Archer", "Skirmisher"];
        // let castle = ["Crossbowman", "Elite Skirmisher", "Cavalry Archer", "NA", "NA", "Thumb Ring"];
        // let imperial = ["Arbalester", "NA", "Heavy Cavalry Archer", "Hand Cannoneer", "NA", "Parthian Tactics"];
        // let postImp = ["Archer", "Skirmisher"];
        // var archer = [2, "Archer", "Crossbowman"Heavy Cavalry Archer, "Arbalester"];
        // var skirm = [2, "Skirmisher", "Elite Skirmisher"];
        // var ca = [3, "Cavalry Archer", "Heavy Cavalry Archer"];
        // var hc = [4, "Hand Cannoneer"];
        
        // this.units.archer = archer;
        // this.units.skirm = skirm;
        // this.units.ca = ca;
        // this.units.hc = hc;

        // this.units.push(archer, skirm, ca, hc);
        let x = [];
        x.push(dark, feudal, castle, imperial, postImp);
        this.setState({units : x});
        console.log(this.state.units);
    }

    generateUnit(i) {
        if (this.props.units !== undefined) {
            return this.props.units[i].map(u => {
                let uImg = u.toLowerCase().replace(' ', '_').replace(' ', '_');
                let classTag = this.determineUnitClassName(this.props.fu, u);
                    let img = <>
                            <Tooltip 
                                overlayClassName="u-tooltip"
                                align={{
                                    offset: ["0 px", "-100 px"],
                                  }}
                                mouseLeaveDelay="0.1"
                                destroyTooltipOnHide="true"
                                placement="top" 
                                overlay={this.tooltipContent()}>
                            <img 
                                className={classTag} name={u}
                                src={require("../Units/" + uImg + ".png")}
                                onMouseEnter={this.handleUnitHover}
                                >

                            </img>
                            </Tooltip>
                            
                            
                            </>
                return (img); 
            }) 
        }
        
    }

    tooltipContent() {
        return <div>{this.state.unitInfo + this.state.unitStats}</div>;
    }

    /* determine & return className of img icon based on regular, fully upgraded, or NA */
    determineUnitClassName(fu, u) {
        let classTag;
        if (fu !== undefined && this.props.fu.includes(u)) {
            classTag = "unit unit-fu"; 
        } else if (u !== NA) {
            classTag = "unit"; 
        } else {
            classTag = "unit unit-na";
        }
        return classTag;
    }

    handleUnitHover(event) {
        let obj = {name: event.target.name, x: event.clientX, y: event.clientY}
        let name = event.target.name;
        let type = "units";
        if (name !== undefined && name !== NA) {
            let unit_id = DATA.name_key.units[name];
            type = "units";
            if (unit_id === undefined) {
                unit_id = DATA.name_key.techs[name];
                type = "techs";
            }

            let unit_info = DATA.key_value[unit_id];
            let unit_stats = this.extractStats(name, type);
            this.setState({unit : event.target.name, unitInfo : unit_info, unitStats : unit_stats});

        } else {
            console.log("NA Hovered!");
            type = NA;
            this.setState({unit : null, unitInfo : null, unitStats : null});
        }
        
        untiService.unitHovered(obj);
    }

    extractStats(name, type) {
        try {
            if (type === "units") {
                var unit_name = DATA.meta.units[name];
                var unit_stats = {
                    HP: unit_name.HP,
                    Attack: unit_name.Attack,
                    Armor: unit_name.MeleeArmor + "/" + unit_name.PierceArmor,
                    Range: unit_name.Range
                }
            } else if (type === "techs") {
                unit_name = DATA.meta.techs[name];
                unit_stats = {
                    Cost: unit_name.Cost
                } 
            } else {
                unit_stats = null;
                console.log("Neither tech or unit stats");
            }
            console.log(unit_stats);
            return unit_stats;
        } catch (e) {
            console.log(e);
        }
        
    }

    render() {
        return(
         
            <div className="tree">
            <div><img src={require("../Techs/dark_age.png")} className="age"></img>
                {this.generateUnit(0)}
                
            </div>
            <div><img src={require("../Techs/feudal_age.png")} className="age"></img>
                {this.generateUnit(1)}

            </div>
            <div><img src={require("../Techs/castle_age.png")} className="age"></img>
                {this.generateUnit(2)}

            </div>
            <div><img src={require("../Techs/imperial_age.png")} className="age"></img>
                {this.generateUnit(3)}
            </div>
            <div><img src={require("../Techs/imperial_age.png")} className="age age-empty"></img>
                {this.generateUnit(4)}

            </div>
            {/* <ReactTooltip className="tooltip" place="top" type="dark" effect="float">
            </ReactTooltip> */}
            
            </div>
            
        )
    }
}

export default Tree;
