import React, {Component} from "react";
import TreePanel from "./treePanel";
import {TOWNCENTRE, RANGE, BARRACKS, STABLE, SIEGE, CASTLE, BLACSKMITH, MONASTERY, UNIVERSITY, ECONOMY, DOCK, DEFENSE} from "./UNIT_NAMES";
import CivContext from "../CivContext";
const NA = "NA";

var num = 1;

export default class TreeParent extends Component {
    xRange = Array.from(RANGE);
    // xRange = [[NA], ["Archer", "Skirmisher"], ["Crossbowman", "Elite Skirmisher", "Cavalry Archer", NA, NA, "Thumb Ring"], ["Arbalester", NA, "Heavy Cavalry Archer", "Hand Cannoneer", NA, "Parthian Tactics"], [NA]];
    
    
    constructor(props) {
        super(props);
        this.state = {civ: "Aztecs"};
        this.updateTree = this.updateTree.bind(this);
        console.log("State!");
        
    }

    /* value = civ selected */
    updateTree(value) {

        console.log(value)
        this.xRange = Array.from(RANGE);

        switch (value) {
            case "Berbers":
                this.disable(this.xRange, ["Cavalry Archer", "Hand Cannoneer", "Thumb Ring", "Parthian Tactics", "Heavy Cavalry Archer"]);
                break;
            case "Turks":
                this.disable(this.xRange, ["Arbalester", "Elite Skirmisher"]);
                break;
        }

        if (num > 1) {
            this.setState({civ: value});
        }

        num++;
  
        
    }

    disable(build, units) {
        console.log(RANGE);
        let newTree = Array.from(build);
        let disableLength = units.length;

        for (let i=0; disableLength>0 && i<=4; i++) {
            newTree[i].forEach(unit => {
                units.forEach(x => {
                    if (unit === x) {
                        let k = newTree[i].indexOf(unit);
                        newTree[i][k] = NA;
                        disableLength--;
                    } 
                });
            });
        }        
        console.log(newTree);
    }

    render() {
        
        return(
            <>
            <CivContext.Consumer> 
                {value => this.updateTree(value)}
            </CivContext.Consumer> 
            <div>
                <h5> HEADING </h5>
                <table>
                    <tbody>
                        <tr>
                        <TreePanel build="Archery Range" tree={RANGE} key={RANGE}/></tr>
                        {/* <TreePanel build="Barracks" tree={barracks}/>
                        <TreePanel build="Stable" tree={stable}/>
                        
                        </tr>
                        <tr>
                        <TreePanel build="Siege Workshop" tree={siege}/>
                        <TreePanel build="Castle" tree={castle}/>
                        <TreePanel build="Defense" tree={defense}/>
                        </tr>
                        <tr>
                        <TreePanel build="Blacksmith" tree={blacksmith}/>
                        <TreePanel build="University" tree={university}/>
                        <TreePanel build="Monastery" tree={monastery}/>
                        </tr>
                        <tr>
                        <TreePanel build="Town Center" tree={townCentre}/>
                        <TreePanel build="Economic Buildings" tree={economy}/>
                        <TreePanel build="Dock" tree={dock}/>
                        </tr> */}
                    </tbody>
                </table>
            </div>
            </>
                    
            
        )
        
    }
}
