import React, {Component} from "react";
import MemberList from "./memberList";

const memberList = new MemberList();

class MemberPanel extends Component {
    constructor(props) {
        super(props);
        this.state = {members: memberList.getMemberList(), count : memberList.getTotalCount(), showList: false};
        this.showMembers = this.showMembers.bind(this);
        this.displayMembers = this.displayMembers.bind(this);
         
    }

    getMembers() {
        return this.state.count;
    }

    showMembers() {
        if (!this.state.showList) {
            this.setState({showList : true});
            console.log(this.state.showList);
            return this.state.members;
        }
    }

    async displayMembers() {
        let x = "";
        if (this.state.showList) {
            this.state.members.forEach(m => {
                console.log(m.elo);
                x += <p key={m.id}> {"[" + m.elo + "] " + m.name} </p>
            })
        }

        return x;
    }

    render() {
        return (
            <div>
                <p>Members online: {this.getMembers()} <button onClick={this.showMembers} className="btn btn-dark">Show Members</button></p>
                <p>{this.displayMembers()}</p>
            </div>
        )
    }
}

export default MemberPanel;
